import re
import unicodedata

### FROM: https://stackoverflow.com/a/518232/3025981
def strip_accents(s):
    return ''.join(c for c in unicodedata.normalize('NFD', s)
                   if unicodedata.category(c) != 'Mn')
### END FROM

def clean_text(s):
    return re.sub("[^a-z \n]", "", strip_accents(s))

def get_freqs(text, relative=False):
    ### BEGIN SOLUTION
    freqs = {}
    for character in text:
        freqs[character] = freqs.get(character, 0) + 1
    if relative:
        for character in freqs:
            freqs[character] /= len(text)
    return freqs
    ### END SOLUTION

### BEGIN SOLUTION
from math import factorial
def prod(iterable):
    p = 1
    for item in iterable:
        p *= item
    return p

def multinomial_likelihood(probs, freqs):
    coeff = factorial(sum(freqs.values())) / prod(factorial(freq) 
                                         for freq in freqs.values())
    return coeff * prod(probs[character] ** freqs[character]
                        for character in freqs)
### END SOLUTION

### BEGIN SOLUTION
def multinomial_likelihood_without_coeff(probs, freqs):
    return prod(probs[character] ** freqs[character]
                for character in freqs)
### END SOLUTION

### BEGIN SOLUTION
from math import log
def log_likelihood_without_coeff(probs, freqs):
    return sum(freqs[character] * log(probs[character]) 
               for character in freqs)
### END SOLUTION

### BEGIN SOLUTION
lang_to_count = {'English': 6090, 'Italian': 1611, 'Spanish': 1602, 'German': 2439,
                 'French': 2222, 'Polish': 1412, 'Portuguese': 1034}
lang_to_prior = {lang: count / sum(lang_to_count.values())
                 for lang, count in lang_to_count.items()}
### END SOLUTION

### BEGIN SOLUTIONS
def bayesian_posterior(text, lang_to_probs, lang_to_prior, test_lang):
    freqs = get_freqs(clean_text(text))
    lang_to_likelihood = {}
    for lang, probs in lang_to_probs.items():
        lang_to_likelihood[lang] = multinomial_likelihood_without_coeff(probs, freqs)
    evidence = sum(lang_to_likelihood[lang] * lang_to_prior[lang] for lang in lang_to_probs)
    return lang_to_likelihood[test_lang] * lang_to_prior[test_lang] / evidence
### END SOLUTIONS
